Situation.ConditionCode:
			        using System.Collections;
                    using System.Collections.Generic;

					public void Execute()
					{
					    ConditionList(//accessible faction list);
					}

				    public bool Condition(List<Faction> factions)
					{
					    var fanatics = new List<Faction>();
						var heretics = new List<Faction>();
					    foreach(var faction in factions)
						{
						    foreach(var religion in faction.Religions)
							{
							    if((float)religion.Value / (float)faction.Population >= .85)
                                {
								    fanatics.Add(faction);									
								}
								if((float)religion.Value / (float)faction.Population < .15)
								{
								    heretics.Add(faction);
								}
								foreach(var fanatic in fanatics)
								{
								    foreach(var heretic in heretics)
									{
									    fanatic.Relationships[heretic] - 100;
									}
								}
							}
						}
					}
